$(document).ready(function() {
    $("form").submit(function() {
        var a = $(this).attr("id");
        var b = $("#" + a);
        $.ajax({
            type: "POST",
            url: "../php/mail.php",
            data: b.serialize(),
            success: function(c) {
                $(b).html(c)
            },
            error: function(d, e, c) {
                $(b).html(c)
            }
        });
        return false
    })
});

// Радиокнопки
function activateRadio(option) {
    var radios = document.getElementsByName("radios");
    
    // Установите состояние выбранной радиокнопки в зависимости от выбранной опции
    switch (option) {
      case 1:
        radios[0].checked = true;
        break;
      case 2:
        radios[1].checked = true;
        break;
      case 3:
        radios[2].checked = true;
        break;
      case 4:
        radios[3].checked = true;
        break;
      default:
        break;
    }

    // Вызовите функцию обновления textarea
    updateTextarea();
  }
  
function updateTextarea() {
    var radios = document.getElementsByName("radios");
    var textarea = document.getElementById("output");

    for (var i = 0; i < radios.length; i++) {
    if (radios[i].checked) {
        var selectedOption = radios[i].value;

        // В зависимости от выбранной опции, поместите нужный текст в textarea
        switch (selectedOption) {
        case "option1":
            textarea.value = "Узнать мой номер договора";
            break;
        case "option2":
            textarea.value = "Прошу разблокировать мои счета";
            break;
        case "option3":
            textarea.value = "Рассрочка";
            break;
        case "option4":
            textarea.value = " ";
            break;
        default:
            textarea.value = "";
            break;
        }
    }
    }
}
